package basics;

public class SwitchCaseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=7;
		
		switch(a)
		{
		case 1:
			System.out.println("JAN");
		    break;
		case 2:
			System.out.println("FEB");
			break;
		case 3:
			System.out.println("MARCH");
			break;
			
			default:
				System.out.println("Invalid Choice..");
				break;
		}
		
	}

}
